function getNodeIcon(part, idx) {
  if (part.name.includes('Nouns') || part.name.includes('Noun')) return Icons.book;
  if (part.name.includes('Verbs') || part.name.includes('Verb')) return Icons.vocab;
  if (part.name.includes('Particle') || part.name.includes('Preposition')) return Icons.grammar;
  return NODE_ICONS[idx % NODE_ICONS.length];
}

